SELECT Avg(ArrDelay) as x , absolute_error(x),relative_error(x) FROM FlightData fd, Flight fl , airlineRef where fl.uniqueCarrier = airlineRef.Code and (substring(airlineRef.description, 1, 1) = 'S' or substring(airlineRef.description, 1, 1) = 's' ) and fl.FlighTieStr = fd.FlighTieStr with error ;
